// PERMISSION SYSTEM HELPERS
// ============================================

// Get user's combined permissions (role permissions + user overrides)
async function getUserPermissions(DB: D1Database, userId: number): Promise<string[]> {
  try {
    const result = await DB.prepare(`
      SELECT DISTINCT p.permission_key
      FROM permissions p
      WHERE p.permission_id IN (
        -- From role
        SELECT rp.permission_id
        FROM users u
        JOIN roles r ON u.role_id = r.role_id
        JOIN role_permissions rp ON r.role_id = rp.role_id
        WHERE u.user_id = ?
        
        UNION
        
        -- From direct user permissions (overrides)
        SELECT up.permission_id
        FROM user_permissions up
        WHERE up.user_id = ? AND up.is_granted = 1
      )
    `).bind(userId, userId).all()
    
    return result.results.map((row: any) => row.permission_key)
  } catch (error) {
    console.error('Get user permissions error:', error)
    return []
  }
}

// Check if user has a specific permission
async function checkPermission(DB: D1Database, userId: number, requiredPermission: string): Promise<boolean> {
  const permissions = await getUserPermissions(DB, userId)
  return permissions.includes(requiredPermission)
}

// Get user's resource access (restaurant/beach IDs they can access)
async function getUserResourceAccess(DB: D1Database, userId: number, resourceType: 'restaurant' | 'beach'): Promise<number[]> {
  try {
    const result = await DB.prepare(`
      SELECT resource_id
      FROM user_resource_access
      WHERE user_id = ? AND resource_type = ?
    `).bind(userId, resourceType).all()
    
    return result.results.map((row: any) => row.resource_id)
  } catch (error) {
    console.error('Get user resource access error:', error)
    return []
  }
}

// Middleware: Require authentication
async function requireAuth(c: any, next: () => Promise<void>) {
  const userId = c.req.header('X-User-ID')
  
  if (!userId) {
    return c.json({ error: 'Unauthorized' }, 401)
  }
  
  c.set('userId', parseInt(userId))
  await next()
}

// Middleware: Require specific permission
function requirePermission(permissionSlug: string) {
  return async (c: any, next: () => Promise<void>) => {
    const userId = c.get('userId') || parseInt(c.req.header('X-User-ID') || '0')
    const { DB } = c.env
    
    if (!userId) {
      return c.json({ error: 'Unauthorized' }, 401)
    }
    
    const hasPermission = await checkPermission(DB, userId, permissionSlug)
    
    if (!hasPermission) {
      return c.json({ error: 'Forbidden - Insufficient permissions' }, 403)
    }
    
    await next()
  }
}

// Middleware: Require resource access (for restaurant/beach scoping)
function requireResourceAccess(resourceType: 'restaurant' | 'beach') {
  return async (c: any, next: () => Promise<void>) => {
    const userId = c.get('userId') || parseInt(c.req.header('X-User-ID') || '0')
    const { DB } = c.env
    const resourceId = parseInt(c.req.param('offering_id') || c.req.param('beach_id') || '0')
    
    if (!userId || !resourceId) {
      return c.json({ error: 'Unauthorized' }, 401)
    }
    
    // Check if user has resource-level restrictions
    const allowedResources = await getUserResourceAccess(DB, userId, resourceType)
    
    // If no restrictions defined, allow access to all
    if (allowedResources.length === 0) {
      await next()
      return
    }
    
    // Otherwise, check if user has access to this specific resource
    if (!allowedResources.includes(resourceId)) {
      return c.json({ error: 'Forbidden - No access to this resource' }, 403)
    }
    
    await next()
  }
}

// Middleware: Validate and extract authenticated user's property_id
function getAuthenticatedPropertyId(c: any): string | null {
  const property_id = c.req.header('X-Property-ID')
  if (!property_id) {
    console.error('Missing X-Property-ID header in authenticated request')
  }
  return property_id
}

// Middleware: Check if property has access to a feature (subscription-based)
async function hasFeatureAccess(DB: any, property_id: string, feature_key: string): Promise<boolean> {
  try {
    // Get property's active subscription
    const subscription = await DB.prepare(`
      SELECT ps.*, sp.plan_id
      FROM property_subscriptions ps
      JOIN subscription_plans sp ON ps.plan_id = sp.plan_id
      WHERE ps.property_id = ? AND ps.status = 'active'
      ORDER BY ps.created_at DESC
      LIMIT 1
    `).bind(property_id).first()
    
    if (!subscription) {
      return false // No active subscription
    }
    
    // Check for feature override first
    const override = await DB.prepare(`
      SELECT is_enabled 
      FROM property_feature_overrides pfo
      JOIN feature_flags ff ON pfo.feature_id = ff.feature_id
      WHERE pfo.property_id = ? AND ff.feature_key = ?
        AND (pfo.expires_at IS NULL OR pfo.expires_at > datetime('now'))
    `).bind(property_id, feature_key).first()
    
    if (override) {
      return override.is_enabled === 1
    }
    
    // Check if feature is included in plan
    const planFeature = await DB.prepare(`
      SELECT pf.is_included
      FROM plan_features pf
      JOIN feature_flags ff ON pf.feature_id = ff.feature_id
      WHERE pf.plan_id = ? AND ff.feature_key = ? AND pf.is_included = 1
    `).bind(subscription.plan_id, feature_key).first()
    
    return !!planFeature
  } catch (error) {
    console.error('Feature access check error:', error)
    return false
  }
}

// Middleware: Require feature access
function requireFeature(feature_key: string) {
  return async (c: any, next: any) => {
    const { DB } = c.env
    const property_id = getAuthenticatedPropertyId(c)
    
    if (!property_id) {
      return c.json({ error: 'Unauthorized - No property ID' }, 401)
    }
    
    const hasAccess = await hasFeatureAccess(DB, property_id, feature_key)
    
    if (!hasAccess) {
      return c.json({ 
        error: 'Feature not available', 
        message: `Your subscription plan does not include ${feature_key}. Please upgrade to access this feature.`
      }, 403)
    }
    
    await next()
  }
}

