// Add new hotel
app.post('/api/superadmin/hotels', async (c) => {
  const { DB } = c.env
  const data = await c.req.json()
  
  try {
    // Create hotel property
    const property = await DB.prepare(`
      INSERT INTO properties (name, slug, contact_email, contact_phone, address, status)
      VALUES (?, ?, ?, ?, ?, 'active')
    `).bind(
      data.property_name || data.name,
      data.slug,
      data.contact_email,
      data.contact_phone || null,
      data.location || data.address || null
    ).run()
    
    // Create admin user account in users table (for login)
    const adminEmail = data.admin_email || data.contact_email
    const adminFirstName = data.admin_first_name || data.property_name || data.name
    const adminLastName = data.admin_last_name || 'Admin'
    const adminPassword = data.admin_password || 'admin123'
    
    const userResult = await DB.prepare(`
      INSERT INTO users (email, password_hash, first_name, last_name, role, role_id, property_id, status)
      VALUES (?, ?, ?, ?, 'admin', 2, ?, 'active')
    `).bind(
      adminEmail,
      adminPassword,
      adminFirstName,
      adminLastName,
      property.meta.last_row_id
    ).run()
    
    // Create guest record (for guest-related operations)
    const adminResult = await DB.prepare(`
      INSERT INTO guests (property_id, email, first_name, last_name, phone, preferred_language)
      VALUES (?, ?, ?, ?, ?, 'en')
    `).bind(
      property.meta.last_row_id,
      adminEmail,
      adminFirstName,
      adminLastName,
      data.contact_phone || ''
    ).run()
    
    // Generate registration code for the hotel
    const code = Math.random().toString(36).substring(2, 10).toUpperCase()
    const expiry = new Date()
    expiry.setDate(expiry.getDate() + 30)
    
    await DB.prepare(`
      UPDATE properties 
      SET registration_code = ?, registration_code_expires_at = ?
      WHERE property_id = ?
    `).bind(code, expiry.toISOString(), property.meta.last_row_id).run()
    
    return c.json({
      success: true,
      property_id: property.meta.last_row_id,
      registration_code: code,
      message: 'Hotel created successfully'
    })
  } catch (error) {
    console.error('Create hotel error:', error)
    return c.json({ error: 'Failed to create hotel' }, 500)
  }
})

// Delete hotel (SuperAdmin only)
app.delete('/api/superadmin/hotels/:property_id', async (c) => {
  const { DB } = c.env
  const { property_id } = c.req.param()
  
  try {
    // Note: This is a soft delete - sets status to 'deleted'
    // To prevent data integrity issues, we don't actually delete the record
    await DB.prepare(`
      UPDATE properties 
      SET status = 'deleted', 
          updated_at = CURRENT_TIMESTAMP
      WHERE property_id = ?
    `).bind(property_id).run()
    
    return c.json({
      success: true,
      message: 'Hotel deleted successfully'
    })
  } catch (error) {
    console.error('Delete hotel error:', error)
    return c.json({ error: 'Failed to delete hotel' }, 500)
  }
})

