// Get all tickets
app.get('/api/superadmin/tickets', async (c) => {
  const { DB } = c.env
  const status = c.req.query('status') || 'all'
  const priority = c.req.query('priority') || 'all'
  
  try {
    let query = `SELECT * FROM support_tickets WHERE 1=1`
    const bindings = []
    
    if (status !== 'all') {
      query += ` AND status = ?`
      bindings.push(status)
    }
    if (priority !== 'all') {
      query += ` AND priority = ?`
      bindings.push(priority)
    }
    
    query += ` ORDER BY 
      CASE priority 
        WHEN 'urgent' THEN 1 
        WHEN 'high' THEN 2 
        WHEN 'medium' THEN 3 
        ELSE 4 
      END,
      created_at DESC
      LIMIT 500`
    
    const tickets = await DB.prepare(query).bind(...bindings).all()
    return c.json({ success: true, tickets: tickets.results })
  } catch (error) {
    console.error('Get tickets error:', error)
    return c.json({ error: 'Failed to load tickets' }, 500)
  }
})

