// Create new user
app.post('/api/admin/users', requirePermission('users_create'), async (c) => {
  const { DB } = c.env
  const userData = await c.req.json()
  
  // CRITICAL: Get property_id from authenticated user's header
  const property_id = getAuthenticatedPropertyId(c)
  if (!property_id) {
    return c.json({ error: 'Unauthorized - No property ID' }, 401)
  }
  
  try {
    // Check if email already exists
    const existing = await DB.prepare(`
      SELECT user_id FROM users WHERE email = ?
    `).bind(userData.email).first()
    
    if (existing) {
      return c.json({ error: 'Email already exists' }, 400)
    }
    
    // Create user (in production, hash password!)
    const result = await DB.prepare(`
      INSERT INTO users (
        email, password_hash, first_name, last_name,
        role, role_id, property_id, status, created_at
      ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, CURRENT_TIMESTAMP)
    `).bind(
      userData.email,
      userData.password, // TODO: Hash this!
      userData.first_name,
      userData.last_name,
      'staff', // Default role value for role column
      userData.role_id || null,
      property_id,  // Use authenticated property_id
      userData.status
    ).run()
    
    const userId = result.meta.last_row_id
    
    // Assign custom permissions if provided
    if (userData.permissions && userData.permissions.length > 0) {
      for (const permissionId of userData.permissions) {
        await DB.prepare(`
          INSERT INTO user_permissions (user_id, permission_id, is_granted)
          VALUES (?, ?, 1)
        `).bind(userId, permissionId).run()
      }
    }
    
    return c.json({ success: true, user_id: userId })
  } catch (error) {
    console.error('Create user error:', error)
    console.error('Error details:', error.message, error.stack)
    return c.json({ error: 'Internal server error', details: error.message }, 500)
  }
})

// Update user
app.put('/api/admin/users/:id', requirePermission('users_edit'), async (c) => {
  const { DB } = c.env
  const userId = c.req.param('id')
  const userData = await c.req.json()
  
  // CRITICAL: Get property_id from authenticated user's header
  const property_id = getAuthenticatedPropertyId(c)
  if (!property_id) {
    return c.json({ error: 'Unauthorized - No property ID' }, 401)
  }
  
  try {
    // Update user basic info - ONLY if user belongs to this property
    await DB.prepare(`
      UPDATE users 
      SET first_name = ?, last_name = ?, email = ?,
          role_id = ?, status = ?, updated_at = CURRENT_TIMESTAMP
      WHERE user_id = ? AND property_id = ?
    `).bind(
      userData.first_name,
      userData.last_name,
      userData.email,
      userData.role_id || null,
      userData.status,
      userId,
      property_id  // Ensure can only update own hotel's users
    ).run()
    
    // Update custom permissions
    if (userData.permissions) {
      // Delete existing custom permissions
      await DB.prepare(`
        DELETE FROM user_permissions WHERE user_id = ?
      `).bind(userId).run()
      
      // Insert new permissions
      for (const permissionId of userData.permissions) {
        await DB.prepare(`
          INSERT INTO user_permissions (user_id, permission_id, is_granted)
          VALUES (?, ?, 1)
        `).bind(userId, permissionId).run()
      }
    }
    
    return c.json({ success: true })
  } catch (error) {
    console.error('Update user error:', error)
    return c.json({ error: 'Internal server error' }, 500)
  }
})

// Delete user
app.delete('/api/admin/users/:id', requirePermission('users_delete'), async (c) => {
  const { DB } = c.env
  const userId = c.req.param('id')
  
  // CRITICAL: Get property_id from authenticated user's header
  const property_id = getAuthenticatedPropertyId(c)
  if (!property_id) {
    return c.json({ error: 'Unauthorized - No property ID' }, 401)
  }
  
  try {
    // Prevent deleting user ID 1 (super admin)
    if (parseInt(userId) === 1) {
      return c.json({ error: 'Cannot delete super admin' }, 400)
    }
    
    // ONLY delete if user belongs to this property
    await DB.prepare(`
      DELETE FROM users WHERE user_id = ? AND property_id = ?
    `).bind(userId, property_id).run()
    
    return c.json({ success: true })
  } catch (error) {
    console.error('Delete user error:', error)
    return c.json({ error: 'Internal server error' }, 500)
  }
})

