// Admin login
app.post('/api/admin/login', async (c) => {
  const { DB } = c.env
  const { email, password } = await c.req.json()

  try {
    const user = await DB.prepare(`
      SELECT u.*, r.role_name FROM users u
      LEFT JOIN roles r ON u.role_id = r.role_id
      WHERE u.email = ? AND u.status = 'active'
    `).bind(email).first()

    if (!user) {
      return c.json({ error: 'Invalid credentials' }, 401)
    }

    // Note: In production, use proper bcrypt password verification
    
    // Get user's permissions
    const permissions = await getUserPermissions(DB, user.user_id)
    
    // Get resource access
    const restaurantAccess = await getUserResourceAccess(DB, user.user_id, 'restaurant')
    const beachAccess = await getUserResourceAccess(DB, user.user_id, 'beach')
    
    // Smart redirect: Direct users to their primary workspace
    let redirectTo = '/admin/dashboard'; // Default for full admins
    
    // If user has limited permissions, redirect to their specific workspace
    // permissions is already an array of strings (permission keys)
    const hasFullAccess = permissions.includes('frontdesk_view') || 
                         permissions.includes('analytics_view') || 
                         permissions.includes('users_manage') ||
                         permissions.length > 10; // Full admins have 40+ permissions
    
    if (!hasFullAccess) {
      // Redirect to their primary workspace based on permissions
      if (permissions.some(p => p && p.startsWith('beach_'))) {
        redirectTo = '/admin/beach-management';
      } else if (permissions.some(p => p && p.startsWith('restaurant_'))) {
        redirectTo = '/admin/restaurant-management'; // You can create this later
      } else if (permissions.includes('frontdesk_view')) {
        redirectTo = '/admin/dashboard?tab=frontdesk';
      }
    }
    
    return c.json({
      success: true,
      user: {
        user_id: user.user_id,
        email: user.email,
        first_name: user.first_name,
        last_name: user.last_name,
        role: user.role,
        role_name: user.role_name,
        property_id: user.property_id
      },
      permissions,
      resource_access: {
        restaurants: restaurantAccess,
        beaches: beachAccess
      },
      redirectTo,
      token: `admin-${user.user_id}-${Date.now()}`
    })
  } catch (error) {
    console.error('Admin login error:', error)
    return c.json({ error: 'Internal server error' }, 500)
  }
})

// Admin dashboard stats
app.get('/api/admin/dashboard', async (c) => {
  const { DB } = c.env
  const property_id = c.req.header('X-Property-ID')

  try {
    // Today's bookings
    const todayBookings = await DB.prepare(`
      SELECT COUNT(*) as count FROM bookings
      WHERE property_id = ? AND activity_date = date('now')
    `).bind(property_id).first()

    // This month's revenue
    const monthRevenue = await DB.prepare(`
      SELECT SUM(total_price) as revenue FROM bookings
      WHERE property_id = ? 
        AND strftime('%Y-%m', activity_date) = strftime('%Y-%m', 'now')
        AND payment_status = 'paid'
    `).bind(property_id).first()

    // Active vendors
    const activeVendors = await DB.prepare(`
      SELECT COUNT(*) as count FROM vendor_properties vp
      JOIN vendors v ON vp.vendor_id = v.vendor_id
      WHERE vp.property_id = ? AND v.status = 'active'
    `).bind(property_id).first()

    // Guest engagement (QR scans vs bookings)
    const scans = await DB.prepare(`
      SELECT COUNT(*) as count FROM analytics_events
      WHERE property_id = ? AND event_type = 'qr_scan'
        AND date(timestamp) >= date('now', '-30 days')
    `).bind(property_id).first()

    const bookings = await DB.prepare(`
      SELECT COUNT(*) as count FROM bookings
      WHERE property_id = ? AND created_at >= datetime('now', '-30 days')
    `).bind(property_id).first()

    const engagement = scans.count > 0 ? Math.round((bookings.count / scans.count) * 100) : 0

    return c.json({
      kpis: {
        today_bookings: todayBookings.count,
        month_revenue: monthRevenue.revenue || 0,
        active_vendors: activeVendors.count,
        engagement_rate: engagement
      }
    })
  } catch (error) {
    console.error('Admin dashboard error:', error)
    return c.json({ error: 'Internal server error' }, 500)
  }
})

// Get property's subscription and enabled features (for hotel admins)
app.get('/api/admin/subscription', async (c) => {
  const { DB } = c.env
  const property_id = c.req.query('property_id')
  
  if (!property_id) {
    return c.json({ error: 'Missing property_id' }, 400)
  }
  
  try {
    // Get subscription info
    const subscription = await DB.prepare(`
      SELECT 
        ps.*,
        sp.plan_key,
        sp.plan_name,
        sp.description,
        sp.price_monthly,
        sp.max_rooms,
        sp.max_staff_users,
        sp.max_vendors
      FROM property_subscriptions ps
      JOIN subscription_plans sp ON ps.plan_id = sp.plan_id
      WHERE ps.property_id = ?
      ORDER BY ps.created_at DESC
      LIMIT 1
    `).bind(property_id).first()
    
    if (!subscription) {
      return c.json({ 
        error: 'No subscription found',
        message: 'Please contact support to set up your subscription'
      }, 404)
    }
    
    // Get all features with access status
    const features = await DB.prepare(`
      SELECT 
        ff.feature_key,
        ff.feature_name,
        ff.description,
        ff.category,
        CASE 
          WHEN pfo.override_id IS NOT NULL THEN pfo.is_enabled
          ELSE COALESCE(pf.is_included, 0)
        END as is_enabled,
        pfo.override_reason
      FROM feature_flags ff
      LEFT JOIN plan_features pf ON ff.feature_id = pf.feature_id 
        AND pf.plan_id = ?
      LEFT JOIN property_feature_overrides pfo ON ff.feature_id = pfo.feature_id 
        AND pfo.property_id = ?
        AND (pfo.expires_at IS NULL OR pfo.expires_at > datetime('now'))
      WHERE ff.is_active = 1
      ORDER BY ff.category, ff.feature_name
    `).bind(subscription.plan_id, property_id).all()
    
    // Current usage stats
    const usage = await DB.prepare(`
      SELECT 
        (SELECT COUNT(*) FROM rooms WHERE property_id = ?) as current_rooms,
        (SELECT COUNT(*) FROM users WHERE property_id = ?) as current_staff_users,
        (SELECT COUNT(*) FROM vendor_properties WHERE property_id = ?) as current_vendors
    `).bind(property_id, property_id, property_id).first()
    
    return c.json({
      subscription,
      features: features.results,
      usage,
      limits: {
        max_rooms: subscription.max_rooms,
        max_staff_users: subscription.max_staff_users,
        max_vendors: subscription.max_vendors
      }
    })
  } catch (error) {
    console.error('Get subscription error:', error)
    return c.json({ error: 'Internal server error' }, 500)
  }
})

