// Get all vendors
app.get('/api/admin/vendors', async (c) => {
  const { DB } = c.env
  const property_id = c.req.query('property_id')

  try {
    let query = `
      SELECT 
        v.*,
        c.name_en as category_name
      FROM vendors v
      LEFT JOIN categories c ON v.category_id = c.category_id
    `
    
    // If property_id provided, filter by vendor-property relationship
    if (property_id) {
      query += `
        INNER JOIN vendor_properties vp ON v.vendor_id = vp.vendor_id
        WHERE vp.property_id = ?
      `
    }
    
    query += ` ORDER BY v.created_at DESC`
    
    const vendors = property_id 
      ? await DB.prepare(query).bind(property_id).all()
      : await DB.prepare(query).all()

    return c.json(vendors.results)
  } catch (error) {
    console.error('Get vendors error:', error)
    return c.json({ error: 'Internal server error' }, 500)
  }
})

// Create vendor (Admin)
app.post('/api/admin/vendors', async (c) => {
  const { DB } = c.env
  const data = await c.req.json()

  try {
    // Generate unique slug from business name
    const baseSlug = data.business_name.toLowerCase().replace(/[^a-z0-9]+/g, '-').replace(/(^-|-$)/g, '')
    
    // Check if slug exists and add number if needed
    let slug = baseSlug
    let counter = 1
    while (true) {
      const existing = await DB.prepare('SELECT vendor_id FROM vendors WHERE slug = ?').bind(slug).first()
      if (!existing) break
      slug = `${baseSlug}-${counter}`
      counter++
    }
    
    // Use provided password or default
    const password = data.password || 'vendor123'

    // Check if email already exists
    const existing = await DB.prepare(`
      SELECT vendor_id FROM vendors WHERE email = ?
    `).bind(data.email).first()
    
    if (existing) {
      return c.json({ error: 'Email already registered' }, 400)
    }

    const result = await DB.prepare(`
      INSERT INTO vendors (
        business_name, slug, description_en, category_id,
        contact_person, email, phone, password_hash,
        commission_rate, status
      ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, 'active')
    `).bind(
      data.business_name,
      slug,
      data.description_en || '',
      data.category_id || null,
      data.contact_person || '',
      data.email,
      data.phone || '',
      password,
      data.commission_rate || 0
    ).run()

    const vendor_id = result.meta.last_row_id

    // Link vendor to property (required) - use authenticated property_id
    const property_id = getAuthenticatedPropertyId(c)
    if (!property_id) {
      return c.json({ error: 'Unauthorized - No property ID' }, 401)
    }
    await DB.prepare(`
      INSERT INTO vendor_properties (vendor_id, property_id, custom_commission_rate, joined_via)
      VALUES (?, ?, ?, 'admin')
    `).bind(vendor_id, property_id, data.commission_rate || 15).run()

    return c.json({ 
      success: true, 
      vendor_id,
      message: 'Vendor created successfully. Default password: vendor123'
    })
  } catch (error) {
    console.error('Create vendor error:', error)
    return c.json({ error: 'Failed to create vendor', details: error.message }, 500)
  }
})

