function getElementIcon(type: string) {
  const icons: Record<string, string> = {
    'buffet': 'fa-utensils',
    'bar': 'fa-glass-cheers',
    'kitchen': 'fa-fire-burner',
    'entrance': 'fa-door-open',
    'exit': 'fa-door-closed',
    'restroom': 'fa-restroom',
    'window': 'fa-window-maximize',
    'wall': 'fa-border-all',
    'plant': 'fa-leaf',
    'stage': 'fa-microphone'
  }
  return icons[type] || 'fa-shapes'
}

