// AI-powered texture analysis
app.post('/api/admin/restaurant/:offering_id/analyze-textures', async (c) => {
  const { DB } = c.env
  const { offering_id } = c.req.param()
  const body = await c.req.json()
  const { image, property_id } = body
  
  try {
    // Real AI-powered texture analysis
    // Analyze the restaurant image to extract floor and table materials
    const aiPrompt = `Analyze this restaurant interior photo and extract texture information:

1. FLOOR ANALYSIS:
   - Material type (hardwood, tile, marble, carpet, concrete, stone, vinyl, laminate, terrazzo)
   - Primary color (hex code)
   - Secondary color if pattern exists (hex code)
   - Texture description

2. TABLE ANALYSIS:
   - Material type (wood, glass, metal, marble, granite, laminate, plastic)
   - Primary color (hex code)
   - Secondary color if applicable (hex code)
   - Surface finish (polished, matte, glossy, textured)

3. CONFIDENCE SCORE:
   - Overall confidence in analysis (0.0 to 1.0)

Return JSON format:
{
  "floor_texture_type": "material name",
  "floor_color_primary": "#hexcode",
  "floor_color_secondary": "#hexcode or null",
  "floor_description": "brief description",
  "table_texture_type": "material name",
  "table_color_primary": "#hexcode",
  "table_color_secondary": "#hexcode or null",
  "table_description": "brief description",
  "confidence_score": 0.0-1.0
}

Be specific and accurate with hex colors. Look at the dominant surfaces in the image.`

    // Call AI vision analysis
    const aiResponse = await fetch('https://api.openai.com/v1/chat/completions', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
        'Authorization': 'Bearer ' + (c.env.OPENAI_API_KEY || '')
      },
      body: JSON.stringify({
        model: 'gpt-4o',
        messages: [
          {
            role: 'user',
            content: [
              { type: 'text', text: aiPrompt },
              { type: 'image_url', image_url: { url: image } }
            ]
          }
        ],
        max_tokens: 1000,
        temperature: 0.3
      })
    })
    
    const aiResult = await aiResponse.json()
    
    // Parse AI response
    let aiAnalysis
    try {
      const aiText = aiResult.choices[0].message.content
      // Extract JSON from response (might be wrapped in markdown)
      const jsonMatch = aiText.match(/\{[\s\S]*\}/)
      aiAnalysis = jsonMatch ? JSON.parse(jsonMatch[0]) : {
        floor_texture_type: 'hardwood',
        floor_color_primary: '#8B7355',
        floor_color_secondary: '#A0826D',
        table_texture_type: 'wood',
        table_color_primary: '#FFFFFF',
        table_color_secondary: '#F5F5F5',
        confidence_score: 0.5
      }
    } catch (parseError) {
      console.error('AI response parse error:', parseError)
      // Fallback to defaults
      aiAnalysis = {
        floor_texture_type: 'hardwood',
        floor_color_primary: '#8B7355',
        floor_color_secondary: '#A0826D',
        table_texture_type: 'wood',
        table_color_primary: '#FFFFFF',
        table_color_secondary: '#F5F5F5',
        confidence_score: 0.5
      }
    }
    
    // Store the original image and AI results
    const result = await DB.prepare(`
      INSERT INTO restaurant_textures (
        offering_id, 
        property_id, 
        original_image_url,
        floor_texture_type,
        floor_color_primary,
        floor_color_secondary,
        table_texture_type,
        table_color_primary,
        table_color_secondary,
        ai_analysis_data,
        confidence_score,
        is_active,
        floor_opacity,
        table_opacity
      ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?, ?, 0, 0.8, 0.9)
    `).bind(
      offering_id,
      property_id,
      image,  // Store base64 image (in production, upload to R2/S3)
      aiAnalysis.floor_texture_type,
      aiAnalysis.floor_color_primary,
      aiAnalysis.floor_color_secondary,
      aiAnalysis.table_texture_type,
      aiAnalysis.table_color_primary,
      aiAnalysis.table_color_secondary,
      JSON.stringify(aiAnalysis),
      aiAnalysis.confidence_score
    ).run()
    
    // Get the inserted texture
    const texture = await DB.prepare(`
      SELECT * FROM restaurant_textures 
      WHERE texture_id = ?
    `).bind(result.meta.last_row_id).first()
    
    return c.json({ 
      success: true, 
      texture: texture
    })
  } catch (error) {
    console.error('AI texture analysis error:', error)
    return c.json({ error: 'Failed to analyze textures: ' + error.message }, 500)
  }
})

// ============================================================================
// RESTAURANT MENU MANAGEMENT - OCR & TRANSLATION SYSTEM
// ============================================================================

// Get all menus for a restaurant
app.get('/api/admin/restaurant/:offering_id/menus', async (c) => {
  const { DB } = c.env
  const { offering_id } = c.req.param()
  
  try {
    const menus = await DB.prepare(`
      SELECT 
        m.*,
        COUNT(DISTINCT t.translation_id) as translation_count,
        GROUP_CONCAT(DISTINCT t.language_code) as available_languages,
        CASE WHEN EXISTS (SELECT 1 FROM menu_categories WHERE menu_id = m.menu_id) THEN 1 ELSE 0 END as is_parsed
      FROM restaurant_menus m
      LEFT JOIN restaurant_menu_translations t ON m.menu_id = t.menu_id
      WHERE m.offering_id = ?
      GROUP BY m.menu_id
      ORDER BY m.display_order, m.created_at DESC
    `).bind(offering_id).all()
    
    return c.json({ success: true, menus: menus.results })
  } catch (error) {
    return c.json({ error: 'Failed to fetch menus: ' + error.message }, 500)
  }
})

// Upload and process menu image (with OCR)
app.post('/api/admin/restaurant/:offering_id/menus', async (c) => {
  const { DB } = c.env
  const { offering_id } = c.req.param()
  const body = await c.req.json()
  const { menu_name, menu_type, image_urls, original_image_url, base_language } = body
  
  try {
    // Support both old (single image) and new (multiple images) formats
    let imageUrlsToStore = image_urls;
    if (!imageUrlsToStore && original_image_url) {
      // Legacy support: convert single image to array
      imageUrlsToStore = [original_image_url];
    }
    
    // Store as JSON array
    const imageUrlsJson = JSON.stringify(imageUrlsToStore || []);
    
    // Insert menu
    const result = await DB.prepare(`
      INSERT INTO restaurant_menus (
        offering_id, menu_name, menu_type, original_image_url, 
        base_language, ocr_status, created_at
      ) VALUES (?, ?, ?, ?, ?, 'pending', CURRENT_TIMESTAMP)
    `).bind(offering_id, menu_name, menu_type || 'full', imageUrlsJson, base_language || 'en').run()
    
    const menuId = result.meta.last_row_id
    
    // Return menu details
    const menu = await DB.prepare(`
      SELECT * FROM restaurant_menus WHERE menu_id = ?
    `).bind(menuId).first()
    
    return c.json({ success: true, menu, image_count: imageUrlsToStore?.length || 0 })
  } catch (error) {
    return c.json({ error: 'Failed to create menu: ' + error.message }, 500)
  }
})

// Process OCR for a menu (extract text from image - supports multiple images)
app.post('/api/admin/restaurant/menus/:menu_id/process-ocr', async (c) => {
  const { DB } = c.env
  const { menu_id } = c.req.param()
  
  try {
    // Get menu details
    const menu = await DB.prepare(`
      SELECT * FROM restaurant_menus WHERE menu_id = ?
    `).bind(menu_id).first()
    
    if (!menu) {
      return c.json({ error: 'Menu not found' }, 404)
    }
    
    // Parse image URLs (can be single URL string or JSON array)
    let imageUrls = [];
    try {
      imageUrls = JSON.parse(menu.original_image_url);
      if (!Array.isArray(imageUrls)) {
        imageUrls = [menu.original_image_url];
      }
    } catch {
      // If not JSON, treat as single URL
      imageUrls = [menu.original_image_url];
    }
    
    // Update status to processing
    await DB.prepare(`
      UPDATE restaurant_menus 
      SET ocr_status = 'processing' 
      WHERE menu_id = ?
    `).bind(menu_id).run()
    
    // Build content array for OpenAI (text + all images)
    const contentArray = [
      {
        type: 'text',
        text: `Extract ALL text from ${imageUrls.length > 1 ? 'these ' + imageUrls.length + ' menu pages' : 'this restaurant menu image'}. Format it cleanly with:
- Section headers in UPPERCASE
- Menu items with prices clearly formatted
- Descriptions on separate lines
- Maintain the menu structure and organization
- Include ALL text visible in ${imageUrls.length > 1 ? 'all pages' : 'the image'}
${imageUrls.length > 1 ? '- Process pages in order and indicate page breaks with "--- PAGE X ---"' : ''}

Please provide a complete, accurate transcription that preserves the menu's layout and readability.`
      }
    ];
    
    // Add all images to the request
    imageUrls.forEach((imageUrl) => {
      contentArray.push({
        type: 'image_url',
        image_url: { url: imageUrl }
      });
    });
    
    // Call OpenAI Vision API for OCR
    const openaiResponse = await fetch('https://api.openai.com/v1/chat/completions', {
      method: 'POST',
      headers: {
        'Authorization': `Bearer ${c.env.OPENAI_API_KEY || 'sk-proj-demo'}`,
        'Content-Type': 'application/json'
      },
      body: JSON.stringify({
        model: 'gpt-4o-mini',
        messages: [{
          role: 'user',
          content: contentArray
        }],
        max_tokens: 4000
      })
    })
    
    if (!openaiResponse.ok) {
      const errorData = await openaiResponse.json().catch(() => ({ error: 'Unknown error' }))
      console.error('OpenAI error:', errorData)
      throw new Error(`OCR processing failed: ${JSON.stringify(errorData)}`)
    }
    
    const ocrResult = await openaiResponse.json()
    const extractedText = ocrResult.choices[0]?.message?.content || ''
    
    if (!extractedText) {
      throw new Error('No text extracted from image')
    }
    
    // Update menu with extracted text
    await DB.prepare(`
      UPDATE restaurant_menus 
      SET extracted_text = ?,
          ocr_status = 'completed',
          ocr_processed_at = CURRENT_TIMESTAMP
      WHERE menu_id = ?
    `).bind(extractedText, menu_id).run()
    
    // Auto-translate to base language (stores as translation)
    await DB.prepare(`
      INSERT OR REPLACE INTO restaurant_menu_translations (
        menu_id, language_code, translated_text, translation_method, translated_at
      ) VALUES (?, ?, ?, 'ocr', CURRENT_TIMESTAMP)
    `).bind(menu_id, menu.base_language, extractedText).run()
    
    // Auto-translate to all supported languages
    const supportedLanguages = ['en', 'es', 'fr', 'de', 'it', 'pt', 'ru', 'ar', 'zh']
    const languagesToTranslate = supportedLanguages.filter(lang => lang !== menu.base_language)
    
    // Get language information for all languages
    const languages = await DB.prepare(`
      SELECT language_code, language_name_en, language_name_native 
      FROM supported_languages 
      WHERE language_code IN (${languagesToTranslate.map(() => '?').join(',')})
    `).bind(...languagesToTranslate).all()
    
    // Translate to each language
    const translationResults = []
    for (const lang of languages.results) {
      try {
        // Call OpenAI for translation
        const openaiResponse = await fetch('https://api.openai.com/v1/chat/completions', {
          method: 'POST',
          headers: {
            'Authorization': `Bearer ${c.env.OPENAI_API_KEY || 'sk-proj-demo'}`,
            'Content-Type': 'application/json'
          },
          body: JSON.stringify({
            model: 'gpt-4o-mini',
            messages: [{
              role: 'system',
              content: `You are a professional restaurant menu translator. Translate the menu to ${lang.language_name_en} (${lang.language_name_native}). Maintain:
- Section headers and structure
- Accurate food terminology
- Cultural appropriateness
- Price formatting
- Professional tone

Provide ONLY the translated text, preserving the exact format and structure.`
            }, {
              role: 'user',
              content: extractedText
            }],
            max_tokens: 4000,
            temperature: 0.3
          })
        })
        
        if (openaiResponse.ok) {
          const translationResult = await openaiResponse.json()
          const translatedText = translationResult.choices[0].message.content
          
          // Store translation
          await DB.prepare(`
            INSERT OR REPLACE INTO restaurant_menu_translations (
              menu_id, language_code, translated_text, translation_method, translated_at
            ) VALUES (?, ?, ?, 'auto', CURRENT_TIMESTAMP)
          `).bind(menu_id, lang.language_code, translatedText).run()
          
          translationResults.push({ language: lang.language_code, success: true })
        } else {
          translationResults.push({ language: lang.language_code, success: false })
        }
      } catch (error) {
        console.error(`Auto-translate to ${lang.language_code} failed:`, error)
        translationResults.push({ language: lang.language_code, success: false, error: error.message })
      }
    }
    
    return c.json({ 
      success: true, 
      extracted_text: extractedText,
      message: 'OCR processing completed successfully for ' + imageUrls.length + ' image(s). Auto-translated to ' + translationResults.filter(r => r.success).length + ' languages.',
      images_processed: imageUrls.length,
      auto_translated: true,
      translation_results: translationResults
    })
  } catch (error) {
    // Update status to failed
    await DB.prepare(`
      UPDATE restaurant_menus 
      SET ocr_status = 'failed' 
      WHERE menu_id = ?
    `).bind(menu_id).run()
    
    return c.json({ error: 'OCR processing failed: ' + error.message }, 500)
  }
})

