// Update menu design settings (admin)
app.put('/api/admin/restaurant/:offering_id/menu-design', async (c) => {
  const { DB } = c.env
  const { offering_id } = c.req.param()
  const settings = await c.req.json()
  
  try {
    // Build UPDATE query dynamically
    const allowedFields = [
      'layout_style', 'columns_count', 'item_spacing',
      'primary_color', 'secondary_color', 'accent_color', 'background_color', 'text_color', 'price_color',
      'heading_font', 'body_font', 'font_size_base', 'heading_size', 'item_name_size',
      'show_images', 'image_position', 'image_size', 'show_dividers', 'divider_style', 'show_category_icons',
      'show_restaurant_logo', 'header_style', 'show_restaurant_name', 'show_restaurant_description',
      'header_background_color', 'header_text_color',
      'show_descriptions', 'show_prices', 'price_position', 'show_dietary_icons', 'show_spice_level',
      'hover_effect', 'animation_style', 'border_radius', 'shadow_intensity',
      'language_switcher_position', 'language_switcher_style', 'custom_css'
    ]
    
    const updates = []
    const values = []
    
    for (const field of allowedFields) {
      if (settings[field] !== undefined) {
        updates.push(`${field} = ?`)
        values.push(settings[field])
      }
    }
    
    if (updates.length === 0) {
      return c.json({ error: 'No valid fields to update' }, 400)
    }
    
    updates.push('updated_at = CURRENT_TIMESTAMP')
    values.push(offering_id)
    
    await DB.prepare(`
      UPDATE restaurant_menu_design
      SET ${updates.join(', ')}
      WHERE offering_id = ?
    `).bind(...values).run()
    
    // Return updated design
    const design = await DB.prepare(`
      SELECT * FROM restaurant_menu_design WHERE offering_id = ?
    `).bind(offering_id).first()
    
    return c.json({ success: true, design })
  } catch (error) {
    console.error('Update menu design error:', error)
    return c.json({ error: 'Failed to update menu design' }, 500)
  }
})

// Get guest menu display (with translations)
app.get('/api/restaurant/:offering_id/menu-display', async (c) => {
  const { DB } = c.env
  const { offering_id } = c.req.param()
  const language = c.req.query('language') || 'en'
  
  try {
    // Get restaurant info
    const restaurant = await DB.prepare(`
      SELECT * FROM hotel_offerings WHERE offering_id = ? AND offering_type = 'restaurant'
    `).bind(offering_id).first()
    
    if (!restaurant) {
      return c.json({ error: 'Restaurant not found' }, 404)
    }
    
    // Get design settings
    let design = await DB.prepare(`
      SELECT * FROM restaurant_menu_design WHERE offering_id = ?
    `).bind(offering_id).first()
    
    if (!design) {
      // Create default design
      await DB.prepare(`
        INSERT INTO restaurant_menu_design (offering_id) VALUES (?)
      `).bind(offering_id).run()
      design = await DB.prepare(`
        SELECT * FROM restaurant_menu_design WHERE offering_id = ?
      `).bind(offering_id).first()
    }
    
    // Get menus with their categories and items
    const menus = await DB.prepare(`
      SELECT * FROM restaurant_menus
      WHERE offering_id = ? AND ocr_status = 'completed'
      ORDER BY display_order, created_at
    `).bind(offering_id).all()
    
    const menusWithContent = []
    
    for (const menu of menus.results) {
      // Get translated menu text if available
      let menuText = menu.extracted_text
      const translation = await DB.prepare(`
        SELECT translated_text FROM restaurant_menu_translations
        WHERE menu_id = ? AND language_code = ?
      `).bind(menu.menu_id, language).first()
      
      if (translation && translation.translated_text) {
        menuText = translation.translated_text
      }
      
      // Get categories for this menu
      const categories = await DB.prepare(`
        SELECT * FROM menu_categories
        WHERE menu_id = ?
        ORDER BY display_order
      `).bind(menu.menu_id).all()
      
      const categoriesWithItems = []
      
      for (const category of categories.results) {
        // Get translated category name/desc
        let catName = category.category_name
        let catDesc = category.category_description
        
        const catTrans = await DB.prepare(`
          SELECT * FROM menu_category_translations
          WHERE category_id = ? AND language_code = ?
        `).bind(category.category_id, language).first()
        
        if (catTrans) {
          catName = catTrans.category_name || catName
          catDesc = catTrans.category_description || catDesc
        }
        
        // Get items
        const items = await DB.prepare(`
          SELECT * FROM menu_items
          WHERE category_id = ? AND is_available = 1
          ORDER BY display_order
        `).bind(category.category_id).all()
        
        const itemsWithTranslations = []
        
        for (const item of items.results) {
          let itemName = item.item_name
          let itemDesc = item.description
          
          const itemTrans = await DB.prepare(`
            SELECT * FROM menu_item_translations
            WHERE item_id = ? AND language_code = ?
          `).bind(item.item_id, language).first()
          
          if (itemTrans) {
            itemName = itemTrans.item_name || itemName
            itemDesc = itemTrans.description || itemDesc
          }
          
          itemsWithTranslations.push({
            ...item,
            item_name: itemName,
            description: itemDesc
          })
        }
        
        categoriesWithItems.push({
          ...category,
          category_name: catName,
          category_description: catDesc,
          items: itemsWithTranslations
        })
      }
      
      menusWithContent.push({
        ...menu,
        extracted_text: menuText,
        categories: categoriesWithItems
      })
    }
    
    // Get ALL supported languages
    const allLanguagesResult = await DB.prepare(`
      SELECT language_code, language_name_en, language_name_native
      FROM supported_languages
      WHERE is_active = 1
      ORDER BY language_name_en
    `).all()
    
    // Add flag emojis to each language
    const flagMap = {
      'ar': 'ðŸ‡¸ðŸ‡¦', 'cs': 'ðŸ‡¨ðŸ‡¿', 'da': 'ðŸ‡©ðŸ‡°', 'de': 'ðŸ‡©ðŸ‡ª', 'el': 'ðŸ‡¬ðŸ‡·', 'en': 'ðŸ‡¬ðŸ‡§', 'es': 'ðŸ‡ªðŸ‡¸',
      'fi': 'ðŸ‡«ðŸ‡®', 'fr': 'ðŸ‡«ðŸ‡·', 'hi': 'ðŸ‡®ðŸ‡³', 'hu': 'ðŸ‡­ðŸ‡º', 'id': 'ðŸ‡®ðŸ‡©', 'it': 'ðŸ‡®ðŸ‡¹', 'ja': 'ðŸ‡¯ðŸ‡µ',
      'ko': 'ðŸ‡°ðŸ‡·', 'nl': 'ðŸ‡³ðŸ‡±', 'no': 'ðŸ‡³ðŸ‡´', 'pl': 'ðŸ‡µðŸ‡±', 'pt': 'ðŸ‡µðŸ‡¹', 'ro': 'ðŸ‡·ðŸ‡´', 'ru': 'ðŸ‡·ðŸ‡º',
      'sv': 'ðŸ‡¸ðŸ‡ª', 'th': 'ðŸ‡¹ðŸ‡­', 'tr': 'ðŸ‡¹ðŸ‡·', 'uk': 'ðŸ‡ºðŸ‡¦', 'vi': 'ðŸ‡»ðŸ‡³', 'zh': 'ðŸ‡¨ðŸ‡³'
    }
    const allLanguages = allLanguagesResult.results.map(lang => ({
      ...lang,
      flag: flagMap[lang.language_code] || 'ðŸŒ'
    }))
    
    // If language is not English, translate structured menu items on-demand
    if (language !== 'en' && menusWithContent.length > 0) {
      // Get language info once
      const langInfo = await DB.prepare(`
        SELECT language_name_en, language_name_native
        FROM supported_languages
        WHERE language_code = ?
      `).bind(language).first()
      
      if (langInfo) {
        for (const menu of menusWithContent) {
          // Translate categories and items on-demand
          for (const category of menu.categories) {
            // Check if category translation exists
            const catTrans = await DB.prepare(`
              SELECT * FROM menu_category_translations
              WHERE category_id = ? AND language_code = ?
            `).bind(category.category_id, language).first()
            
            if (!catTrans && category.category_name) {
              // Translate category name and description
              try {
                const catText = category.category_name + (category.category_description ? '\n' + category.category_description : '')
                const openaiResponse = await fetch('https://api.openai.com/v1/chat/completions', {
                  method: 'POST',
                  headers: {
                    'Authorization': `Bearer ${c.env.OPENAI_API_KEY || 'sk-proj-demo'}`,
                    'Content-Type': 'application/json'
                  },
                  body: JSON.stringify({
                    model: 'gpt-4o-mini',
                    messages: [{
                      role: 'system',
                      content: `Translate this menu category to ${langInfo.language_name_en} (${langInfo.language_name_native}). Keep it concise and professional.`
                    }, {
                      role: 'user',
                      content: catText
                    }],
                    max_tokens: 200,
                    temperature: 0.3
                  })
                })
                
                if (openaiResponse.ok) {
                  const result = await openaiResponse.json()
                  const translated = result.choices[0].message.content
                  const lines = translated.split('\n')
                  const catNameTranslated = lines[0]
                  const catDescTranslated = lines[1] || null
                  
                  // Store translation
                  await DB.prepare(`
                    INSERT OR REPLACE INTO menu_category_translations (
                      category_id, language_code, category_name, category_description
                    ) VALUES (?, ?, ?, ?)
                  `).bind(category.category_id, language, catNameTranslated, catDescTranslated).run()
                  
                  // Update current data
                  category.category_name = catNameTranslated
                  category.category_description = catDescTranslated
                }
              } catch (error) {
                console.error(`Category translation failed:`, error)
              }
            }
            
            // Translate items
            for (const item of category.items) {
              const itemTrans = await DB.prepare(`
                SELECT * FROM menu_item_translations
                WHERE item_id = ? AND language_code = ?
              `).bind(item.item_id, language).first()
              
              if (!itemTrans && item.item_name) {
                try {
                  const itemText = item.item_name + (item.description ? '\n' + item.description : '')
                  const openaiResponse = await fetch('https://api.openai.com/v1/chat/completions', {
                    method: 'POST',
                    headers: {
                      'Authorization': `Bearer ${c.env.OPENAI_API_KEY || 'sk-proj-demo'}`,
                      'Content-Type': 'application/json'
                    },
                    body: JSON.stringify({
                      model: 'gpt-4o-mini',
                      messages: [{
                        role: 'system',
                        content: `Translate this menu item to ${langInfo.language_name_en} (${langInfo.language_name_native}). Keep food terminology accurate.`
                      }, {
                        role: 'user',
                        content: itemText
                      }],
                      max_tokens: 300,
                      temperature: 0.3
                    })
                  })
                  
                  if (openaiResponse.ok) {
                    const result = await openaiResponse.json()
                    const translated = result.choices[0].message.content
                    const lines = translated.split('\n')
                    const itemNameTranslated = lines[0]
                    const itemDescTranslated = lines.slice(1).join('\n').trim() || null
                    
                    // Store translation
                    await DB.prepare(`
                      INSERT OR REPLACE INTO menu_item_translations (
                        item_id, language_code, item_name, description
                      ) VALUES (?, ?, ?, ?)
                    `).bind(item.item_id, language, itemNameTranslated, itemDescTranslated).run()
                    
                    // Update current data
                    item.item_name = itemNameTranslated
                    item.description = itemDescTranslated
                  }
                } catch (error) {
                  console.error(`Item translation failed:`, error)
                }
              }
            }
          }
        }
      }
    }
    
    return c.json({
      success: true,
      restaurant,
      design,
      menus: menusWithContent,
      all_languages: allLanguages,
      current_language: language
    })
  } catch (error) {
    console.error('Get menu display error:', error)
    return c.json({ error: 'Failed to fetch menu display' }, 500)
  }
})

