// API: Get Beach Spots with Booking Status (for live map)
app.get('/api/admin/beach/spots', async (c) => {
  const { DB } = c.env
  const { property_id, date, time_slot } = c.req.query()
  
  if (!property_id) {
    return c.json({ error: 'property_id is required' }, 400)
  }
  
  try {
    const bookingDate = date || new Date().toISOString().split('T')[0]
    
    let query = `
      SELECT 
        s.*,
        z.zone_name,
        bb.booking_status,
        bb.booking_reference,
        bb.guest_name
      FROM beach_spots s
      LEFT JOIN beach_zones z ON s.zone_id = z.zone_id
      LEFT JOIN beach_bookings bb ON s.spot_id = bb.spot_id 
        AND bb.booking_date = ?
        AND bb.booking_status IN ('confirmed', 'checked_in')
    `
    
    const params = [bookingDate]
    
    // Add time slot filter if specified
    if (time_slot && time_slot !== 'all') {
      query += ` AND (bb.slot_type = ? OR bb.slot_type = 'full_day' OR bb.slot_type IS NULL)`
      params.push(time_slot)
    }
    
    query += ` WHERE s.property_id = ? AND s.is_active = 1 ORDER BY s.spot_number`
    params.push(Number(property_id))
    
    const spots = await DB.prepare(query).bind(...params).all()
    
    return c.json({ success: true, spots: spots.results || [] })
  } catch (error) {
    console.error('Get beach spots with booking status error:', error)
    return c.json({ error: 'Failed to get beach spots' }, 500)
  }
})

// API: Create Beach Spot
app.post('/api/admin/beach/spots', requirePermission('beach_zones_manage'), async (c) => {
  const { DB } = c.env
  
  try {
    const body = await c.req.json()
    const {
      property_id,
      zone_id,
      spot_number,
      spot_type,
      position_x,
      position_y,
      max_capacity,
      price_full_day,
      price_half_day,
      price_hourly,
      zone_name
    } = body
    
    // Create default zone if none exists
    let zoneIdToUse = zone_id
    if (!zoneIdToUse) {
      const defaultZone = await DB.prepare(`
        SELECT zone_id FROM beach_zones WHERE property_id = ? LIMIT 1
      `).bind(property_id).first()
      
      if (!defaultZone) {
        const zoneResult = await DB.prepare(`
          INSERT INTO beach_zones (property_id, zone_name, zone_type)
          VALUES (?, 'Main Beach', 'standard')
        `).bind(property_id).run()
        zoneIdToUse = zoneResult.meta.last_row_id
      } else {
        zoneIdToUse = defaultZone.zone_id
      }
    }
    
    await DB.prepare(`
      INSERT INTO beach_spots (
        property_id, zone_id, spot_number, spot_type,
        position_x, position_y, max_capacity,
        price_full_day, price_half_day, price_hourly
      ) VALUES (?, ?, ?, ?, ?, ?, ?, ?, ?, ?)
    `).bind(
      property_id,
      zoneIdToUse,
      spot_number,
      spot_type,
      position_x,
      position_y,
      max_capacity || 2,
      price_full_day || 0,
      price_half_day || 0,
      price_hourly || 0
    ).run()
    
    return c.json({ success: true })
  } catch (error) {
    console.error('Create beach spot error:', error)
    return c.json({ error: 'Failed to create beach spot' }, 500)
  }
})

