      async function loadRoomServiceCardSettings() {
        try {
          const response = await fetchWithAuth('/api/admin/custom-sections?property_id=' + propertyId);
          const data = await response.json();
          const roomServiceSection = data.sections?.find(s => s.section_key === 'room-service');
          
          if (roomServiceSection) {
            document.getElementById('roomServiceCardTitle').value = roomServiceSection.section_name_en || 'Room Service';
            document.getElementById('roomServiceCardSubtitle').value = roomServiceSection.subtitle_en || 'Order from your room';
            document.getElementById('roomServiceCardDescription').value = roomServiceSection.description_en || 'Browse our menu and call to order';
            document.getElementById('roomServiceCardIcon').value = roomServiceSection.icon_class || 'fas fa-concierge-bell';
            
            // Handle color - check if it's a hex color or old color name
            const cardColor = roomServiceSection.color_class || '#6366f1';
            if (cardColor.startsWith('#')) {
              document.getElementById('roomServiceCardColor').value = cardColor;
              document.getElementById('roomServiceCardColorText').value = cardColor;
            } else {
              // Convert old color names to hex
              const colorMap = {
                'indigo': '#6366f1',
                'blue': '#3b82f6',
                'purple': '#8b5cf6',
                'pink': '#ec4899',
                'red': '#ef4444',
                'orange': '#f97316',
                'green': '#10b981',
                'teal': '#14b8a6'
              };
              const hexColor = colorMap[cardColor] || '#6366f1';
              document.getElementById('roomServiceCardColor').value = hexColor;
              document.getElementById('roomServiceCardColorText').value = hexColor;
            }
            
            document.getElementById('roomServiceCardVisible').checked = roomServiceSection.is_visible === 1;
          }
          
          // Initialize icon and color previews
          updateRoomServiceIconPreview();
          updateRoomServiceColorPreview();
          
          // Add event listeners for real-time preview
          document.getElementById('roomServiceCardIcon').addEventListener('change', updateRoomServiceIconPreview);
          document.getElementById('roomServiceCardColor').addEventListener('input', function(e) {
            document.getElementById('roomServiceCardColorText').value = e.target.value;
            updateRoomServiceColorPreview();
          });
          document.getElementById('roomServiceCardColorText').addEventListener('input', function(e) {
            if (/^#[0-9A-Fa-f]{6}$/.test(e.target.value)) {
              document.getElementById('roomServiceCardColor').value = e.target.value;
              updateRoomServiceColorPreview();
            }
          });
        } catch (error) {
          console.error('Load card settings error:', error);
        }
      }
      
      function updateRoomServiceIconPreview() {
        const iconSelect = document.getElementById('roomServiceCardIcon');
        const iconPreview = document.getElementById('roomServiceCardIconPreview');
        if (iconSelect && iconPreview) {
          iconPreview.className = iconSelect.value + ' text-4xl text-purple-600';
        }
      }
      
      function updateRoomServiceColorPreview() {
        const colorPicker = document.getElementById('roomServiceCardColor');
        const colorPreview = document.getElementById('roomServiceCardColorPreview');
        if (colorPicker && colorPreview) {
          const color = colorPicker.value;
          // Create a lighter version for gradient
          const r = parseInt(color.slice(1, 3), 16);
          const g = parseInt(color.slice(3, 5), 16);
          const b = parseInt(color.slice(5, 7), 16);
          const lighterColor = '#' + 
            Math.min(255, r + 40).toString(16).padStart(2, '0') +
            Math.min(255, g + 40).toString(16).padStart(2, '0') +
            Math.min(255, b + 40).toString(16).padStart(2, '0');
          colorPreview.style.background = 'linear-gradient(135deg, ' + color + ' 0%, ' + lighterColor + ' 100%)';
        }
      }
      
      async function toggleRoomService() {
        const isEnabled = document.getElementById('roomServiceEnabled').checked;
        const statusDiv = document.getElementById('roomServiceStatus');
        const disabledDiv = document.getElementById('roomServiceDisabled');
        
        if (isEnabled && !roomServiceOffering) {
          // Create room service offering
          try {
            const response = await fetchWithAuth('/api/admin/offerings', {
              method: 'POST',
              body: JSON.stringify({
                offering_type: 'room_service',
                title_en: 'Room Service',
                short_description_en: 'Enjoy delicious meals and beverages delivered to your room at any time.',
                full_description_en: '24 Hours',
                price: 0,
                location: '',
                duration_minutes: null,
                requires_booking: 0,
                images: JSON.stringify([]),
                video_url: null,
                event_date: null,
                event_start_time: null,
                event_end_time: null,
                menu_urls: null
              })
            });

            const data = await response.json();

            if (data.success) {
              roomServiceOffering = { offering_id: data.offering_id };
              statusDiv.classList.remove('hidden');
              disabledDiv.classList.add('hidden');
              await loadRoomService();
              alert('âœ… Room Service enabled successfully!');
            } else {
              alert('Error: ' + (data.error || 'Failed to enable room service'));
              document.getElementById('roomServiceEnabled').checked = false;
            }
          } catch (error) {
            console.error('Enable room service error:', error);
            alert('Error enabling room service: ' + error.message);
            document.getElementById('roomServiceEnabled').checked = false;
          }
        } else if (!isEnabled && roomServiceOffering) {
          // Delete room service offering
          if (confirm('Are you sure you want to disable Room Service? This will remove the menu from guest access.')) {
            try {
              const response = await fetchWithAuth('/api/admin/offerings/' + roomServiceOffering.offering_id, {
                method: 'DELETE'
              });

              const data = await response.json();

              if (data.success) {
                roomServiceOffering = null;
                statusDiv.classList.add('hidden');
                disabledDiv.classList.remove('hidden');
                alert('âœ… Room Service disabled successfully!');
              } else {
                alert('Error: ' + (data.error || 'Failed to disable room service'));
                document.getElementById('roomServiceEnabled').checked = true;
              }
            } catch (error) {
              console.error('Disable room service error:', error);
              alert('Error disabling room service: ' + error.message);
              document.getElementById('roomServiceEnabled').checked = true;
            }
          } else {
            document.getElementById('roomServiceEnabled').checked = true;
          }
        }
      }
      
