// MENU BUILDER - Admin Interface  
// ========================================
app.get('/admin/menu-builder/:menu_id', async (c) => {
  const { menu_id } = c.req.param()
  
  return c.html(`
<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Menu Builder</title>
    <script src="https://cdn.tailwindcss.com"></script>
    <link href="https://cdn.jsdelivr.net/npm/@fortawesome/fontawesome-free@6.4.0/css/all.min.css" rel="stylesheet">
    <style>
        @keyframes slideUp {
            from { opacity: 0; transform: translateY(20px); }
            to { opacity: 1; transform: translateY(0); }
        }
        .animate-slideUp {
            animation: slideUp 0.3s ease-out;
        }
    </style>
</head>
<body class="bg-gray-50">
    <div class="max-w-7xl mx-auto px-4 py-8">
        <div class="flex items-center justify-between mb-8">
            <div>
                <a href="/admin/restaurant/1" class="text-blue-600 hover:text-blue-800 mb-2 inline-block">
                    <i class="fas fa-arrow-left mr-2"></i>Back to Restaurant
                </a>
                <h1 class="text-3xl font-bold text-gray-900">Menu Builder</h1>
                <p class="text-gray-600 mt-1">Edit your menu - Update prices, descriptions, and organize categories</p>
            </div>
            <div class="flex gap-3">
                <button onclick="saveAllChanges()" class="px-6 py-3 bg-green-600 hover:bg-green-700 text-white rounded-lg font-semibold transition">
                    <i class="fas fa-save mr-2"></i>Save Changes
                </button>
                <a href="/menu/${menu_id}" target="_blank" class="px-6 py-3 bg-blue-600 hover:bg-blue-700 text-white rounded-lg font-semibold transition inline-block">
                    <i class="fas fa-eye mr-2"></i>Preview
                </a>
                <div class="flex-1 text-right text-sm text-gray-500 flex items-center justify-end">
                    <i class="fas fa-language mr-2"></i>
                    <span>Menus auto-translate to all languages</span>
                </div>
            </div>
        </div>

        <div id="menuBuilder" class="space-y-6">
            <div class="text-center py-12">
                <i class="fas fa-spinner fa-spin text-4xl text-gray-400 mb-4"></i>
                <p class="text-gray-600">Loading menu...</p>
            </div>
        </div>
    </div>

    <script>
        const menuId = '${menu_id}';
        let menuData = { categories: [] };

        async function loadMenu() {
            try {
                const response = await fetch('/api/admin/restaurant/menus/' + menuId + '/structure');
                const data = await response.json();
                
                if (data.success) {
                    menuData = data;
                    renderMenuBuilder();
                } else {
                    alert('Error loading menu');
                }
            } catch (error) {
                console.error('Load error:', error);
                alert('Failed to load menu');
            }
        }

        function renderMenuBuilder() {
            const container = document.getElementById('menuBuilder');
            
            if (menuData.categories.length === 0) {
                container.innerHTML = '<div class="bg-white rounded-lg shadow p-12 text-center">' +
                    '<i class="fas fa-utensils text-6xl text-gray-300 mb-4"></i>' +
                    '<p class="text-gray-600 text-lg mb-6">No categories yet. Start building your menu!</p>' +
                    '<button onclick="addCategory()" class="px-8 py-4 bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white rounded-xl transition font-bold text-lg shadow-lg hover:shadow-xl transform hover:scale-105">' +
                        '<i class="fas fa-plus-circle mr-2"></i>Add Your First Category' +
                    '</button>' +
                '</div>';
                return;
            }

            container.innerHTML = menuData.categories.map((category, catIndex) => {
                const items = (category.items || []).map((item, itemIndex) => {
                    let badges = '';
                    if (item.is_vegetarian) badges += '<span class="px-2 py-1 bg-green-100 text-green-800 text-xs rounded-full">ðŸŒ± Veg</span> ';
                    if (item.is_vegan) badges += '<span class="px-2 py-1 bg-green-100 text-green-800 text-xs rounded-full">ðŸŒ¾ Vegan</span> ';
                    if (item.spice_level && item.spice_level !== 'none') badges += '<span class="px-2 py-1 bg-orange-100 text-orange-800 text-xs rounded-full">ðŸŒ¶ï¸ ' + item.spice_level + '</span> ';

                    // Escape HTML to prevent XSS and quote issues
                    const escapeHtml = (str) => String(str).replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;').replace(/"/g, '&quot;').replace(/'/g, '&#039;');
                    
                    return '<div class="border-2 border-gray-200 rounded-lg p-4 hover:border-blue-400 transition">' +
                        '<div class="flex items-start justify-between">' +
                            '<div class="flex-1 mr-4">' +
                                '<input type="text" value="' + escapeHtml(item.item_name || '') + '" data-cat="' + catIndex + '" data-item="' + itemIndex + '" data-field="name" onchange="updateItemFromEvent(this)" class="font-semibold text-gray-900 border-0 border-b border-transparent hover:border-blue-500 focus:border-blue-600 outline-none px-2 py-1 w-full">' +
                                '<textarea data-cat="' + catIndex + '" data-item="' + itemIndex + '" data-field="description" onchange="updateItemFromEvent(this)" class="text-sm text-gray-600 border-0 border-b border-transparent hover:border-gray-300 focus:border-gray-400 outline-none px-2 py-1 w-full mt-2 resize-none" rows="2" placeholder="Description">' + escapeHtml(item.description || '') + '</textarea>' +
                                '<div class="flex gap-2 mt-2">' + badges + '</div>' +
                            '</div>' +
                            '<div class="flex flex-col items-end gap-2">' +
                                '<input type="number" step="0.01" value="' + (item.price || 0) + '" data-cat="' + catIndex + '" data-item="' + itemIndex + '" data-field="price" onchange="updateItemFromEvent(this)" class="text-xl font-bold text-green-600 border-0 border-b border-transparent hover:border-green-500 focus:border-green-600 outline-none px-2 py-1 w-24 text-right">' +
                                '<button onclick="deleteItem(' + item.item_id + ', ' + catIndex + ', ' + itemIndex + ')" class="px-3 py-1 bg-red-100 hover:bg-red-200 text-red-700 rounded text-sm transition">' +
                                    '<i class="fas fa-trash"></i>' +
                                '</button>' +
                            '</div>' +
                        '</div>' +
                    '</div>';
                }).join('');

                const escapeHtml = (str) => String(str).replace(/&/g, '&amp;').replace(/</g, '&lt;').replace(/>/g, '&gt;').replace(/"/g, '&quot;').replace(/'/g, '&#039;');

                return '<div class="bg-white rounded-lg shadow-lg p-6">' +
                    '<div class="flex items-center justify-between mb-4">' +
                        '<div class="flex-1">' +
                            '<input type="text" value="' + escapeHtml(category.category_name || '') + '" data-cat="' + catIndex + '" data-field="name" onchange="updateCategoryFromEvent(this)" class="text-2xl font-bold text-gray-900 border-0 border-b-2 border-transparent hover:border-blue-500 focus:border-blue-600 outline-none px-2 py-1 w-full">' +
                            '<input type="text" value="' + escapeHtml(category.category_description || '') + '" data-cat="' + catIndex + '" data-field="description" onchange="updateCategoryFromEvent(this)" class="text-sm text-gray-600 border-0 border-b border-transparent hover:border-gray-300 focus:border-gray-400 outline-none px-2 py-1 w-full mt-1" placeholder="Category description">' +
                        '</div>' +
                        '<button onclick="deleteCategory(' + category.category_id + ', ' + catIndex + ')" class="ml-4 px-4 py-2 bg-red-100 hover:bg-red-200 text-red-700 rounded-lg transition">' +
                            '<i class="fas fa-trash"></i>' +
                        '</button>' +
                    '</div>' +
                    '<div class="space-y-3">' + items + '</div>' +
                    '<div class="grid grid-cols-2 gap-3 mt-4">' +
                        '<button onclick="addItem(' + category.category_id + ')" class="py-2 bg-green-100 hover:bg-green-200 text-green-700 rounded-lg transition font-semibold">' +
                            '<i class="fas fa-plus mr-2"></i>Add Item' +
                        '</button>' +
                        '<button onclick="translateCategory(' + category.category_id + ')" class="py-2 bg-purple-100 hover:bg-purple-200 text-purple-700 rounded-lg transition font-semibold">' +
                            '<i class="fas fa-language mr-2"></i>Translate' +
                        '</button>' +
                    '</div>' +
                '</div>';
            }).join('') + 
            '<div class="mt-6"><button onclick="addCategory()" class="w-full py-4 bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white rounded-lg transition font-bold text-lg shadow-lg">' +
                '<i class="fas fa-plus-circle mr-2"></i>Add New Category' +
            '</button></div>';
        }

        function updateCategoryFromEvent(element) {
            const catIndex = parseInt(element.getAttribute('data-cat'));
            const field = element.getAttribute('data-field');
            const value = element.value;
            
            if (field === 'name') menuData.categories[catIndex].category_name = value;
            if (field === 'description') menuData.categories[catIndex].category_description = value;
        }

        function updateItemFromEvent(element) {
            const catIndex = parseInt(element.getAttribute('data-cat'));
            const itemIndex = parseInt(element.getAttribute('data-item'));
            const field = element.getAttribute('data-field');
            const value = element.value;
            
            const item = menuData.categories[catIndex].items[itemIndex];
            if (field === 'name') item.item_name = value;
            if (field === 'description') item.description = value;
            if (field === 'price') item.price = parseFloat(value);
        }

        // Backward compatibility
        function updateCategory(catIndex, field, value) {
            if (field === 'name') menuData.categories[catIndex].category_name = value;
            if (field === 'description') menuData.categories[catIndex].category_description = value;
        }

        function updateItem(catIndex, itemIndex, field, value) {
            const item = menuData.categories[catIndex].items[itemIndex];
            if (field === 'name') item.item_name = value;
            if (field === 'description') item.description = value;
            if (field === 'price') item.price = parseFloat(value);
        }

        async function saveAllChanges() {
            const btn = event.target;
            btn.innerHTML = '<i class="fas fa-spinner fa-spin mr-2"></i>Saving...';
            btn.disabled = true;

            try {
                for (const category of menuData.categories) {
                    await fetch('/api/admin/restaurant/menu-categories/' + category.category_id, {
                        method: 'PUT',
                        headers: { 'Content-Type': 'application/json' },
                        body: JSON.stringify({
                            category_name: category.category_name,
                            category_description: category.category_description,
                            display_order: category.display_order
                        })
                    });

                    for (const item of category.items) {
                        await fetch('/api/admin/restaurant/menu-items/' + item.item_id, {
                            method: 'PUT',
                            headers: { 'Content-Type': 'application/json' },
                            body: JSON.stringify(item)
                        });
                    }
                }

                alert('âœ… All changes saved successfully!');
                btn.innerHTML = '<i class="fas fa-save mr-2"></i>Save Changes';
                btn.disabled = false;
            } catch (error) {
                alert('Error saving: ' + error.message);
                btn.innerHTML = '<i class="fas fa-save mr-2"></i>Save Changes';
                btn.disabled = false;
            }
        }

        async function translateCategory(categoryId) {
            const lang = prompt('Enter language code (ar=Arabic, de=German, ru=Russian, es=Spanish, fr=French, it=Italian, zh=Chinese):');
            if (!lang) return;

            try {
                const response = await fetch('/api/admin/restaurant/menu-categories/' + categoryId + '/translate', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify({ target_language: lang })
                });

                const data = await response.json();
                if (data.success) {
                    alert('âœ… ' + data.message);
                } else {
                    alert('Error: ' + data.error);
                }
            } catch (error) {
                alert('Translation error: ' + error.message);
            }
        }

        async function deleteCategory(categoryId, index) {
            if (!confirm('Delete this category and all its items?')) return;

            try {
                const response = await fetch('/api/admin/restaurant/menu-categories/' + categoryId, {
                    method: 'DELETE'
                });

                if (response.ok) {
                    menuData.categories.splice(index, 1);
                    renderMenuBuilder();
                }
            } catch (error) {
                alert('Delete error: ' + error.message);
            }
        }

        async function deleteItem(itemId, catIndex, itemIndex) {
            if (!confirm('Delete this item?')) return;

            try {
                const response = await fetch('/api/admin/restaurant/menu-items/' + itemId, {
                    method: 'DELETE'
                });

                if (response.ok) {
                    menuData.categories[catIndex].items.splice(itemIndex, 1);
                    renderMenuBuilder();
                }
            } catch (error) {
                alert('Delete error: ' + error.message);
            }
        }

        // Add new category
        function addCategory() {
            const modalHtml = '<div class="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center z-50">' +
              '<div class="bg-gradient-to-br from-white to-blue-50 rounded-2xl shadow-2xl p-8 max-w-md w-full mx-4 transform animate-slideUp">' +
                '<div class="flex items-center mb-6">' +
                  '<div class="bg-gradient-to-br from-blue-600 to-indigo-600 text-white rounded-full p-3 mr-4">' +
                    '<i class="fas fa-folder-plus text-2xl"></i>' +
                  '</div>' +
                  '<h2 class="text-2xl font-bold text-gray-900">Add Category</h2>' +
                '</div>' +
                '<div class="space-y-4 mb-6">' +
                  '<div>' +
                    '<label class="block text-sm font-semibold text-gray-700 mb-2">Category Name *</label>' +
                    '<input type="text" id="newCategoryName" placeholder="e.g., Appetizers" value="New Category" class="w-full px-4 py-3 border-2 border-blue-200 rounded-xl focus:border-blue-500 focus:ring-4 focus:ring-blue-100 outline-none transition" autofocus>' +
                  '</div>' +
                  '<div>' +
                    '<label class="block text-sm font-semibold text-gray-700 mb-2">Description</label>' +
                    '<textarea id="newCategoryDescription" placeholder="Brief description..." rows="2" class="w-full px-4 py-3 border-2 border-blue-200 rounded-xl focus:border-blue-500 focus:ring-4 focus:ring-blue-100 outline-none transition resize-none"></textarea>' +
                  '</div>' +
                '</div>' +
                '<div class="flex gap-3">' +
                  '<button id="cancelNewCategory" class="flex-1 px-6 py-3 bg-gray-200 hover:bg-gray-300 text-gray-700 rounded-xl font-semibold transition">Cancel</button>' +
                  '<button id="submitNewCategory" class="flex-1 px-6 py-3 bg-gradient-to-r from-blue-600 to-indigo-600 hover:from-blue-700 hover:to-indigo-700 text-white rounded-xl font-semibold shadow-lg hover:shadow-xl transition-all transform hover:scale-105">' +
                    '<i class="fas fa-check mr-2"></i>Add Category' +
                  '</button>' +
                '</div>' +
              '</div>' +
            '</div>';
            
            const modal = document.createElement('div');
            modal.innerHTML = modalHtml;
            const modalDiv = modal.firstChild;
            document.body.appendChild(modalDiv);
            
            setTimeout(() => {
              document.getElementById('newCategoryName').focus();
              document.getElementById('newCategoryName').select();
            }, 100);
            
            document.getElementById('cancelNewCategory').addEventListener('click', () => {
              document.body.removeChild(modalDiv);
            });
            
            document.getElementById('submitNewCategory').addEventListener('click', async () => {
              const name = document.getElementById('newCategoryName').value.trim();
              const description = document.getElementById('newCategoryDescription').value.trim();
              
              if (!name) {
                alert('âš ï¸ Please enter a category name');
                return;
              }
              
              document.getElementById('submitNewCategory').innerHTML = '<i class="fas fa-spinner fa-spin mr-2"></i>Adding...';
              document.getElementById('submitNewCategory').disabled = true;
              
              try {
                const response = await fetch('/api/admin/restaurant/menus/' + menuId + '/categories', {
                  method: 'POST',
                  headers: { 'Content-Type': 'application/json' },
                  body: JSON.stringify({
                    category_name: name,
                    category_description: description
                  })
                });

                const data = await response.json();
                if (data.success) {
                  document.body.removeChild(modalDiv);
                  loadMenu();
                } else {
                  alert('Error: ' + data.error);
                  document.body.removeChild(modalDiv);
                }
              } catch (error) {
                alert('Error adding category: ' + error.message);
                document.body.removeChild(modalDiv);
              }
            });
        }

        // Add new item to category
        function addItem(categoryId) {
            const modalHtml = '<div class="fixed inset-0 bg-black/60 backdrop-blur-sm flex items-center justify-center z-50">' +
              '<div class="bg-gradient-to-br from-white to-green-50 rounded-2xl shadow-2xl p-8 max-w-md w-full mx-4 transform animate-slideUp">' +
                '<div class="flex items-center mb-6">' +
                  '<div class="bg-gradient-to-br from-green-600 to-emerald-600 text-white rounded-full p-3 mr-4">' +
                    '<i class="fas fa-utensils text-2xl"></i>' +
                  '</div>' +
                  '<h2 class="text-2xl font-bold text-gray-900">Add Menu Item</h2>' +
                '</div>' +
                '<div class="space-y-4 mb-6">' +
                  '<div>' +
                    '<label class="block text-sm font-semibold text-gray-700 mb-2">Item Name *</label>' +
                    '<input type="text" id="newItemName" placeholder="e.g., Caesar Salad" value="New Item" class="w-full px-4 py-3 border-2 border-green-200 rounded-xl focus:border-green-500 focus:ring-4 focus:ring-green-100 outline-none transition" autofocus>' +
                  '</div>' +
                  '<div>' +
                    '<label class="block text-sm font-semibold text-gray-700 mb-2">Description</label>' +
                    '<textarea id="newItemDescription" placeholder="Describe the dish..." rows="2" class="w-full px-4 py-3 border-2 border-green-200 rounded-xl focus:border-green-500 focus:ring-4 focus:ring-green-100 outline-none transition resize-none"></textarea>' +
                  '</div>' +
                  '<div class="grid grid-cols-2 gap-3">' +
                    '<div>' +
                      '<label class="block text-sm font-semibold text-gray-700 mb-2">Price *</label>' +
                      '<input type="number" id="newItemPrice" placeholder="0.00" step="0.01" min="0" value="0" class="w-full px-4 py-3 border-2 border-green-200 rounded-xl focus:border-green-500 focus:ring-4 focus:ring-green-100 outline-none transition">' +
                    '</div>' +
                    '<div>' +
                      '<label class="block text-sm font-semibold text-gray-700 mb-2">Currency</label>' +
                      '<select id="newItemCurrency" class="w-full px-4 py-3 border-2 border-green-200 rounded-xl focus:border-green-500 outline-none transition">' +
                        '<option value="USD">USD $</option>' +
                        '<option value="EUR">EUR â‚¬</option>' +
                        '<option value="GBP">GBP Â£</option>' +
                      '</select>' +
                    '</div>' +
                  '</div>' +
                '</div>' +
                '<div class="flex gap-3">' +
                  '<button id="cancelNewItem" class="flex-1 px-6 py-3 bg-gray-200 hover:bg-gray-300 text-gray-700 rounded-xl font-semibold transition">Cancel</button>' +
                  '<button id="submitNewItem" class="flex-1 px-6 py-3 bg-gradient-to-r from-green-600 to-emerald-600 hover:from-green-700 hover:to-emerald-700 text-white rounded-xl font-semibold shadow-lg hover:shadow-xl transition-all transform hover:scale-105">' +
                    '<i class="fas fa-check mr-2"></i>Add Item' +
                  '</button>' +
                '</div>' +
              '</div>' +
            '</div>';
            
            const modal = document.createElement('div');
            modal.innerHTML = modalHtml;
            const modalDiv = modal.firstChild;
            document.body.appendChild(modalDiv);
            
            setTimeout(() => {
              document.getElementById('newItemName').focus();
              document.getElementById('newItemName').select();
            }, 100);
            
            document.getElementById('cancelNewItem').addEventListener('click', () => {
              document.body.removeChild(modalDiv);
            });
            
            document.getElementById('submitNewItem').addEventListener('click', async () => {
              const name = document.getElementById('newItemName').value.trim();
              const description = document.getElementById('newItemDescription').value.trim();
              const price = parseFloat(document.getElementById('newItemPrice').value) || 0;
              const currency = document.getElementById('newItemCurrency').value;
              
              if (!name) {
                alert('âš ï¸ Please enter an item name');
                return;
              }
              
              document.getElementById('submitNewItem').innerHTML = '<i class="fas fa-spinner fa-spin mr-2"></i>Adding...';
              document.getElementById('submitNewItem').disabled = true;
              
              try {
                const response = await fetch('/api/admin/restaurant/menu-categories/' + categoryId + '/items', {
                  method: 'POST',
                  headers: { 'Content-Type': 'application/json' },
                  body: JSON.stringify({
                    item_name: name,
                    description: description,
                    price: price,
                    currency: currency
                  })
                });

                const data = await response.json();
                if (data.success) {
                  document.body.removeChild(modalDiv);
                  loadMenu();
                } else {
                  alert('Error: ' + data.error);
                  document.body.removeChild(modalDiv);
                }
              } catch (error) {
                alert('Error adding item: ' + error.message);
                document.body.removeChild(modalDiv);
              }
            });
        }

        loadMenu();
    </script>
</body>
</html>
  `)
})

// Feedback submission page - serve HTML directly
app.get('/feedback/:form_id', async (c) => {
  const formId = c.req.param('form_id')
  return c.html(`<!DOCTYPE html>
<html lang="en">
<head>
    <meta charset="UTF-8">
    <meta name="viewport" content="width=device-width, initial-scale=1.0">
    <title>Feedback Form</title>
    <script src="https://cdn.tailwindcss.com"></script>
    <link href="https://cdn.jsdelivr.net/npm/@fortawesome/fontawesome-free@6.4.0/css/all.min.css" rel="stylesheet">
    <style>
        .star-rating { display: flex; gap: 0.5rem; font-size: 2rem; flex-direction: row-reverse; justify-content: flex-end; }
        .star { cursor: pointer; color: #d1d5db; transition: color 0.2s; }
        .star.active { color: #fbbf24; }
        .star:hover, .star:hover ~ .star { color: #fbbf24; }
    </style>
</head>
<body class="bg-gradient-to-br from-purple-50 to-indigo-50 min-h-screen">
    <div class="container mx-auto px-4 py-8 max-w-2xl">
        <div id="loading" class="text-center py-12">
            <i class="fas fa-spinner fa-spin text-4xl text-purple-600 mb-4"></i>
            <p class="text-gray-600">Loading form...</p>
        </div>

        <div id="formContainer" class="hidden">
            <div class="bg-white rounded-2xl shadow-2xl overflow-hidden">
                <div class="bg-gradient-to-r from-purple-600 to-indigo-600 text-white p-8">
                    <h1 class="text-3xl font-bold mb-2" id="formName"></h1>
                    <p class="text-purple-100" id="formDescription"></p>
                    <p class="text-sm text-purple-200 mt-2" id="propertyName"></p>
                </div>

                <div class="p-8">
                    <form id="feedbackForm" class="space-y-6">
                        <div id="guestInfoSection" class="space-y-4 pb-6 border-b">
                            <h3 class="text-lg font-bold text-gray-900">Your Information</h3>
                            <div id="guestFields"></div>
                        </div>

                        <div id="questionsSection" class="space-y-6"></div>

                        <button type="submit" class="w-full bg-gradient-to-r from-purple-600 to-indigo-600 text-white py-4 rounded-xl font-bold text-lg hover:shadow-lg transition-all">
                            <i class="fas fa-paper-plane mr-2"></i>Submit Feedback
                        </button>
                    </form>
                </div>
            </div>
        </div>

        <div id="thankYouMessage" class="hidden">
            <div class="bg-white rounded-2xl shadow-2xl p-12 text-center">
                <div class="mb-6">
                    <i class="fas fa-check-circle text-6xl text-green-500"></i>
                </div>
                <h2 class="text-3xl font-bold text-gray-900 mb-4" id="thankYouText"></h2>
                <p class="text-gray-600 mb-6">We appreciate you taking the time to share your thoughts.</p>
            </div>
        </div>

        <div id="errorMessage" class="hidden">
            <div class="bg-white rounded-2xl shadow-2xl p-12 text-center">
                <i class="fas fa-exclamation-triangle text-6xl text-red-500 mb-4"></i>
                <h2 class="text-2xl font-bold text-gray-900 mb-2">Form Not Available</h2>
                <p class="text-gray-600" id="errorText"></p>
            </div>
        </div>
    </div>

    <script>
        const formId = ${formId};
        let formData = null;

        async function loadForm() {
            try {
                const res = await fetch('/api/feedback/forms/' + formId);
                const data = await res.json();

                if (!data.success) throw new Error(data.error || 'Failed to load form');

                formData = data.form;
                
                document.getElementById('formName').textContent = formData.form_name;
                document.getElementById('formDescription').textContent = formData.form_description || '';
                document.getElementById('propertyName').textContent = formData.property_name;

                const guestFields = document.getElementById('guestFields');
                const fields = [];
                
                if (formData.require_room_number) {
                    fields.push('<div><label class="block text-sm font-semibold mb-2">Room Number *</label><input type="text" name="room_number" required class="w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-600" placeholder="e.g., 303"></div>');
                }
                if (formData.require_guest_name) {
                    fields.push('<div><label class="block text-sm font-semibold mb-2">Your Name *</label><input type="text" name="guest_name" required class="w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-600" placeholder="Full name"></div>');
                }
                if (formData.require_email) {
                    fields.push('<div><label class="block text-sm font-semibold mb-2">Email *</label><input type="email" name="guest_email" required class="w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-600" placeholder="your@email.com"></div>');
                }
                if (formData.require_phone) {
                    fields.push('<div><label class="block text-sm font-semibold mb-2">Phone *</label><input type="tel" name="guest_phone" required class="w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-600" placeholder="+1234567890"></div>');
                }

                guestFields.innerHTML = fields.join('');

                const questionsSection = document.getElementById('questionsSection');
                questionsSection.innerHTML = formData.questions.map((q, idx) => {
                    let inputHtml = '';
                    
                    switch(q.question_type) {
                        case 'text':
                            inputHtml = '<input type="text" name="q_' + q.question_id + '" ' + (q.is_required ? 'required' : '') + ' class="w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-600" placeholder="Your answer">';
                            break;
                        case 'textarea':
                            inputHtml = '<textarea name="q_' + q.question_id + '" rows="4" ' + (q.is_required ? 'required' : '') + ' class="w-full px-4 py-3 border rounded-lg focus:ring-2 focus:ring-purple-600" placeholder="Your answer"></textarea>';
                            break;
                        case 'rating':
                            inputHtml = '<div class="star-rating" data-question="' + q.question_id + '">';
                            for (let i = 5; i >= 1; i--) {
                                inputHtml += '<span class="star" data-value="' + i + '" onclick="setRating(' + q.question_id + ', ' + i + ')">â˜…</span>';
                            }
                            inputHtml += '</div><input type="hidden" name="q_' + q.question_id + '" ' + (q.is_required ? 'required' : '') + '>';
                            break;
                        case 'scale':
                            inputHtml = '<div class="flex justify-between items-center gap-2">';
                            for (let i = 1; i <= 10; i++) {
                                inputHtml += '<button type="button" onclick="setScale(' + q.question_id + ', ' + i + ')" class="scale-btn w-12 h-12 border-2 rounded-lg font-bold hover:bg-purple-600 hover:text-white hover:border-purple-600 transition-all" data-question="' + q.question_id + '" data-value="' + i + '">' + i + '</button>';
                            }
                            inputHtml += '</div><input type="hidden" name="q_' + q.question_id + '" ' + (q.is_required ? 'required' : '') + '>';
                            break;
                        case 'multiple_choice':
                            const options = q.options || [];
                            inputHtml = '<div class="space-y-2">';
                            options.forEach(opt => {
                                inputHtml += '<label class="flex items-center space-x-3 p-3 border rounded-lg hover:bg-purple-50 cursor-pointer transition-all"><input type="radio" name="q_' + q.question_id + '" value="' + opt + '" ' + (q.is_required ? 'required' : '') + ' class="w-5 h-5 text-purple-600"><span class="flex-1">' + opt + '</span></label>';
                            });
                            inputHtml += '</div>';
                            break;
                        case 'yes_no':
                            inputHtml = '<div class="flex gap-4">';
                            inputHtml += '<label class="flex-1 flex items-center justify-center space-x-2 p-4 border-2 rounded-lg hover:bg-green-50 hover:border-green-600 cursor-pointer transition-all"><input type="radio" name="q_' + q.question_id + '" value="Yes" ' + (q.is_required ? 'required' : '') + ' class="w-5 h-5"><span class="font-semibold">Yes</span></label>';
                            inputHtml += '<label class="flex-1 flex items-center justify-center space-x-2 p-4 border-2 rounded-lg hover:bg-red-50 hover:border-red-600 cursor-pointer transition-all"><input type="radio" name="q_' + q.question_id + '" value="No" ' + (q.is_required ? 'required' : '') + ' class="w-5 h-5"><span class="font-semibold">No</span></label>';
                            inputHtml += '</div>';
                            break;
                        case 'nps':
                            inputHtml = '<div class="space-y-3"><div class="flex justify-between text-sm text-gray-600 mb-2"><span>Not at all likely</span><span>Extremely likely</span></div><div class="grid grid-cols-11 gap-2">';
                            for (let i = 0; i <= 10; i++) {
                                inputHtml += '<button type="button" onclick="setNPS(' + q.question_id + ', ' + i + ')" class="nps-btn aspect-square border-2 rounded-lg font-bold hover:bg-purple-600 hover:text-white hover:border-purple-600 transition-all" data-question="' + q.question_id + '" data-value="' + i + '">' + i + '</button>';
                            }
                            inputHtml += '</div></div><input type="hidden" name="q_' + q.question_id + '" ' + (q.is_required ? 'required' : '') + '>';
                            break;
                    }
                    
                    return '<div class="question-block"><label class="block text-md font-semibold text-gray-900 mb-3">' + q.question_text + (q.is_required ? ' <span class="text-red-500">*</span>' : '') + '</label>' + inputHtml + '</div>';
                }).join('');

                document.getElementById('loading').classList.add('hidden');
                document.getElementById('formContainer').classList.remove('hidden');
            } catch (error) {
                console.error('Load form error:', error);
                document.getElementById('loading').classList.add('hidden');
                document.getElementById('errorMessage').classList.remove('hidden');
                document.getElementById('errorText').textContent = error.message;
            }
        }

        function setRating(questionId, value) {
            document.querySelector('[name="q_' + questionId + '"]').value = value;
            const stars = document.querySelectorAll('[data-question="' + questionId + '"] .star');
            stars.forEach(star => {
                star.classList.toggle('active', parseInt(star.dataset.value) <= value);
            });
        }

        function setScale(questionId, value) {
            document.querySelector('[name="q_' + questionId + '"]').value = value;
            document.querySelectorAll('[data-question="' + questionId + '"].scale-btn').forEach(btn => {
                btn.classList.toggle('bg-purple-600', parseInt(btn.dataset.value) === value);
                btn.classList.toggle('text-white', parseInt(btn.dataset.value) === value);
                btn.classList.toggle('border-purple-600', parseInt(btn.dataset.value) === value);
            });
        }

        function setNPS(questionId, value) {
            document.querySelector('[name="q_' + questionId + '"]').value = value;
            document.querySelectorAll('[data-question="' + questionId + '"].nps-btn').forEach(btn => {
                btn.classList.toggle('bg-purple-600', parseInt(btn.dataset.value) === value);
                btn.classList.toggle('text-white', parseInt(btn.dataset.value) === value);
                btn.classList.toggle('border-purple-600', parseInt(btn.dataset.value) === value);
            });
        }

        document.getElementById('feedbackForm').addEventListener('submit', async (e) => {
            e.preventDefault();
            const formElement = e.target;
            const formDataObj = new FormData(formElement);
            
            const answers = [];
            formData.questions.forEach(q => {
                const answer = formDataObj.get('q_' + q.question_id);
                if (answer) {
                    answers.push({
                        question_id: q.question_id,
                        answer_text: answer
                    });
                }
            });

            const submission = {
                form_id: formId,
                room_number: formDataObj.get('room_number') || null,
                guest_name: formDataObj.get('guest_name') || null,
                guest_email: formDataObj.get('guest_email') || null,
                guest_phone: formDataObj.get('guest_phone') || null,
                answers: answers,
                source: 'homepage'
            };

            try {
                const res = await fetch('/api/feedback/submit', {
                    method: 'POST',
                    headers: { 'Content-Type': 'application/json' },
                    body: JSON.stringify(submission)
                });

                const data = await res.json();

                if (data.success) {
                    document.getElementById('formContainer').classList.add('hidden');
                    document.getElementById('thankYouMessage').classList.remove('hidden');
                    document.getElementById('thankYouText').textContent = formData.thank_you_message || 'Thank you for your feedback!';
                } else {
                    alert('Failed to submit: ' + data.error);
                }
            } catch (error) {
                console.error('Submit error:', error);
                alert('Failed to submit feedback. Please try again.');
            }
        });

        loadForm();
    </script>
</body>
</html>
  `)
})

